CREATE SCHEMA dictionaries;

CREATE TABLE dictionaries.gender (
    gender_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    gender varchar NOT NULL
    );
    VALUES ('Male'),('Female'),('Other'),('Anonymous');  

CREATE TABLE dictionaries.roles (
    role_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    role varchar NOT NULL,
    description varchar NOT NULL
    );
    VALUES ('Superadmin','Ability to directly modify database'),('Admin','Limited ability to modify database'),('Authorizer','Can approve data entries from Enterer role'),('Enterer','Can enter data into the database'),('Person','Has no stated privileges');

CREATE TABLE dictionaries.countries (
    country_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    abbreviation varchar NOT NULL,
    full_name varchar NOT NULL
    );

CREATE TABLE dictionaries.taxonomy_rank (
    taxononmy_rank_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    taxonomy_rank varchar NOT NULL
    );
    VALUES ('subspecies'),('species'),('subgenus'),('genus'),('subtribe'),('tribe'),('subfamily'),('family'),('superfamily'),('infraorder'),('suborder'),('superorder'),('infraclass'),('subclass'),('class'),('superclass'),('subphylum'),('phylum'),('superphylum'),('subkingdom'),('kingdom'),('superkingdom'),('unranked clade'),('unranked');

CREATE TABLE dictionaries.refs_types (
    refs_type_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    refs_type varchar
    );
    VALUES ('journal article'),('standalone book'),('edited book of contributed articles'),('contributed article in edited book'),('unpublished');

CREATE TABLE dictionaries.taxonomy_relationship (
    taxonomy_relationship_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    taxonomy_relationship varchar
    );
    VALUES ('belongs to'),('subjective synonym of'),('objective synonym of'),('invalid subgroup of'),('misspelling of'),('replaced by'),('nomen dubium'),('nomen nudum'),('nomen oblitum'),('nomen vanum');

CREATE TABLE dictionaries.taxonomy_confidence (
    taxonomy_confidence_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    taxonomy_confidence varchar,
    taxonomy_confidence_definition varchar
    );
    VALUES ('accepted','An accepted name is a valid name that is not a junior synonym.'),('superseded','A valid name that is a junior synonym.'),('operational','An informal name that can confidently be used as an operational taxonomic unit.'),('tentative','An informal name that is not recommended for general use as an operational taxonomic unit.'),('invalid','A name that is formally considered invalid.');

CREATE TABLE person (
    person_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    given varchar NOT NULL,
    surname varchar NOT NULL,
    middle varchar,
    email varchar,
    password varchar,
    orcid varchar,
    role_no integer REFERENCES dictionaries.roles("role_no") NOT NULL,
    role_parent integer REFERENCES dictionaries.roles("role_no"),
    gender_no integer REFERENCES dictionaries.gender("gender_no") NOT NULL,
    country_no integer REFERENCES dictionaries.countries("country_no") NOT NULL,
    institution varchar,
    active boolean,
    total_hours numeric
    );
    
 CREATE TABLE refs (
     refs_vers integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
     refs_no integer NOT NULL, -- This is a permanent id that will stay constant regardless of version
     refs_type_no integer REFERENCES dictionaries.refs_types("refs_type_no"),
     authorizer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever was the authorizer of the enterer
     enterer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever made the entry or edit
     ref_json jsonb, -- references JSON PBot node
     preceded_by integer REFERENCES refs("refs_vers"),
     succeeded_by integer REFERENCES refs("refs_vers"),
     removed boolean
     );

 CREATE TABLE collections (
     collection_vers integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
     collection_no integer NOT NULL, -- This is a permanent id that will stay constant regardless of version
     authorizer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever was the authorizer of the enterer
     enterer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever made the entry or edit
     collection_json jsonb, -- Collection json -- taken directly from PBot!!!
     refs_no integer NOT NULL, -- A single refs is considered the "primary". 
     preceded_by integer REFERENCES collections("collection_vers"),
     succeeded_by integer REFERENCES collections("collection_vers"),
     removed boolean
     );

-- A lookup table for additional refs (called refs_secondary in original db)
CREATE TABLE refs_additional (
    refs_additional_id integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    collection_no integer REFERENCES collections("collection_no"),
    refs_no integer REFERENCES refs("refs_no")
    );

-- The opinion system in PBDB is one of its most important and unique components
-- We need to preserve all of its current functionality while also introducing
-- Some more flexibility for PBot, namely the inclusion of more types of
-- taxonomic_relatiobnship 
CREATE TABLE opinion (
    opinion_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    authorizer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever was the authorizer of the enterer
    enterer_no integer REFERENCES person("person_no") NOT NULL, -- Whoever made the entry or edit
    refs_no integer REFERENCES refs("refs_no") NOT NULL,
    related_taxon_no integer REFERENCES taxon("taxon_no") NOT NULL,
    taxonomy_relationship_no integer REFERENCES dictionaries.taxonomy_relationship("taxonomy_relationship_no"),
    preceded_by integer REFERENCES opinion("collection_vers"),
    succeeded_by integer REFERENCES opinion("collection_vers")
    );

-- I am positing here a complete rework of the taxon table in that it only exists 
-- as a derived outcome based on logical analysis of the opinion table
-- therefore no user ever adds a new taxon, they only ever add a new opinion
-- from which we derive the existence of the taxon
CREATE TABLE taxon (
    taxon_no integer GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    taxonomy_confidence integer REFERENCES dictionaries.taxonomy_confidence("taxonomy_confidence_no"),
    taxononmy_rank_no integer REFERENCES dictionaries.taxonomy_confidence("taxonomy_rank_no")
    );

